mapper.select:
SELECT M_NO, M_ID, M_PW, M_NICKNAME, M_TYPE, M_AUTH, M_IMG, M_IMG_PATH, M_RESUMECHK
FROM IT_MEMBER
WHERE M_ID = #{m_id}
AND M_PW = #{m_pw}

SELECT COUNT(*)
FROM IT_MEMBER
WHERE M_ID = #{m_id}

SELECT COUNT(*)
FROM IT_MEMBER
WHERE M_EMAIL = #{m_email}

SELECT COUNT(*)
FROM BIZ_MEMBER
WHERE M_REGNO = #{m_regno}

SELECT COUNT (*)
FROM IT_MEMBER
WHERE M_ID = #{m_id}
AND M_PW = #{m_pw}

mapper.update:
UPDATE IT_MEMBER
SET M_PW = #{m_pw}, M_NICKNAME = #{m_nickname}, M_EMAIL = #{m_email}, M_PHONE = #{m_phone}
WHERE M_NO = #{m_no}

UPDATE IT_MEMBER
SET M_IMG = #{m_img}, M_IMG_PATH = #{m_img_path}
WHERE M_NO = #{m_no}
