mapper.select:
SELECT M_NO, M_ID, M_PW, M_NICKNAME, M_TYPE, M_AUTH, M_IMG, M_IMG_PATH, M_RESUMECHK
FROM IT_MEMBER
WHERE M_ID = #{m_id}
AND M_PW = #{m_pw}

SELECT COUNT(*)
FROM IT_MEMBER
WHERE M_ID = #{m_id}

SELECT COUNT(*)
FROM IT_MEMBER
WHERE M_EMAIL = #{m_email}

SELECT COUNT(*)
FROM BIZ_MEMBER
WHERE M_REGNO = #{m_regno}

SELECT COUNT (*)
FROM IT_MEMBER
WHERE M_ID = #{m_id}
AND M_PW = #{m_pw}

mapper.update:
UPDATE IT_MEMBER
SET M_PW = #{m_pw}, M_NICKNAME = #{m_nickname}, M_EMAIL = #{m_email}, M_PHONE = #{m_phone}
WHERE M_NO = #{m_no}

UPDATE IT_MEMBER
SET M_IMG = #{m_img}, M_IMG_PATH = #{m_img_path}
WHERE M_NO = #{m_no}

mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT C.NAME, B.BC_CODE, B.BD_NO, B.BD_TITLE, DECODE(RE.REPLYCOUNT,NULL,0,RE.REPLYCOUNT) BD_REPLYCOUNT, B.BD_VIEWCOUNT, DECODE(BR.RECOMMANDCOUNT,NULL,0,BR.RECOMMANDCOUNT) BD_RECOMMANDCOUNT,B.BD_CREATEDDATE FROM BOARD B, BOARDCATEGORY C, (SELECT BD_NO,COUNT(BD_NO) REPLYCOUNT FROM REPLY GROUP BY BD_NO) RE, (SELECT BD_NO,COUNT(BD_NO) RECOMMANDCOUNT FROM BOARDRECOMMAND GROUP BY BD_NO) BR WHERE B.BC_CODE = C.CODE(+)
AND B.BC_CODE = #{category}
AND B.M_NO = #{m_no} AND B.BD_NO = RE.BD_NO(+) AND B.BD_NO = BR.BD_NO(+) ORDER BY B.BD_CREATEDDATE DESC, B.BD_NO DESC) A ORDER BY RN ASC) WHERE RN BETWEEN #{start} AND #{end}

		SELECT COUNT(BD_NO) 
		FROM BOARD 
		WHERE M_NO=#{m_no} 

        AND BC_CODE = #{category}
