mapper.select:
SELECT * FROM (SELECT ROWNUM RN, B.BD_NO, S.ST_STATUS, S.ST_ADDR1, S.ST_ADDR2, S.ST_NOWPERSON, S.ST_CLOSEPERSON, B.BD_TITLE, DECODE(RE.REPLYCOUNT,NULL,0,RE.REPLYCOUNT) BD_REPLYCOUNT, B.BD_VIEWCOUNT, DECODE(BR.RECOMMANDCOUNT,NULL,0,BR.RECOMMANDCOUNT) BD_RECOMMANDCOUNT, M.M_IMG_PATH, M.M_IMG,M.M_NICKNAME,B.BD_CREATEDDATE FROM BOARD B, STUDY S, IT_MEMBER M , (SELECT BD_NO,COUNT(BD_NO) REPLYCOUNT FROM REPLY GROUP BY BD_NO) RE, (SELECT BD_NO,COUNT(BD_NO) RECOMMANDCOUNT FROM BOARDRECOMMAND GROUP BY BD_NO) BR WHERE B.BD_NO = S.BD_NO AND B.BC_CODE=3 AND B.M_NO = M.M_NO AND RE.BD_NO(+) = B.BD_NO AND BR.BD_NO(+) = B.BD_NO
AND S.ST_ADDR1 LIKE '%${studySearchDto.a1}%'
AND S.ST_ADDR2 LIKE '%${studySearchDto.a2}%'
AND S.ST_STATUS = #{studySearchDto.stat}
AND (B.BD_TITLE LIKE '%${studySearchDto.key}%' OR B.BD_CONTENT LIKE '%${studySearchDto.key}%')
ORDER BY B.BD_CREATEDDATE DESC) WHERE RN BETWEEN #{start} AND #{end}
